import React, { useState, useContext } from "react";
import { TransactionContext } from "../contexts/TransactionContext";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";

const TransactionList = () => {
  const { transactions } = useContext(TransactionContext);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("date");
  const [typeFilter, setTypeFilter] = useState("all"); // "all", "expense", or "income"

  // Filter transactions based on search input and type
  const filteredTransactions = transactions.filter((transaction) => {
    const matchesSearch = transaction.description
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
    
    const matchesType = 
      typeFilter === "all" ? true :
      typeFilter === "expense" ? transaction.amount < 0 :
      transaction.amount >= 0;
    
    return matchesSearch && matchesType;
  });

  // Sort transactions by date or amount
  const sortedTransactions = [...filteredTransactions].sort((a, b) => {
    if (sortBy === "amount") return b.amount - a.amount;
    return new Date(b.date) - new Date(a.date);
  });

  // Prepare data for visualization
  const categories = {};
  
  filteredTransactions.forEach((txn) => {
    const category = txn.category;
    if (!categories[category]) categories[category] = 0;
    categories[category] += Math.abs(txn.amount);
  });
  
  const chartData = Object.keys(categories).map((category) => ({
    name: category,
    value: categories[category],
  }));

  // Colors based on type filter
  const CHART_COLOR = typeFilter === "expense" ? "#FF0000" : "#00C853";
  
  // Generate shades of the main color for variety
  const getColorShades = (baseColor, count) => {
    const shades = [];
    
    if (baseColor === "#FF0000") { // Red shades for expenses
      for (let i = 0; i < count; i++) {
        const intensity = Math.max(0, 255 - (i * 40));
        shades.push(`rgb(255, ${intensity}, ${intensity})`);
      }
    } else { // Green shades for income
      for (let i = 0; i < count; i++) {
        const intensity = Math.max(0, 255 - (i * 40));
        shades.push(`rgb(${i * 20}, ${intensity}, ${i * 20})`);
      }
    }
    
    return shades;
  };

  const colorShades = getColorShades(CHART_COLOR, chartData.length);

  return (
    <div>
      <h2>Transactions</h2>
      
      <div className="filters grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
        {/* Search Bar */}
        <input
          type="text"
          placeholder="Search transactions..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="border p-2 rounded-md w-full"
        />
        
        {/* Sorting Options */}
        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="border p-2 rounded-md w-full"
        >
          <option value="date">Sort by Date</option>
          <option value="amount">Sort by Amount</option>
        </select>
        
        {/* Type Filter - THIS IS THE KEY ADDITION */}
        <select
          value={typeFilter}
          onChange={(e) => setTypeFilter(e.target.value)}
          className="border p-2 rounded-md w-full"
        >
          <option value="all">All Transactions</option>
          <option value="expense">Expenses Only</option>
          <option value="income">Income Only</option>
        </select>
      </div>

      {/* Transactions List */}
      <ul className="mb-8">
        {sortedTransactions.map((transaction) => (
          <li 
            key={transaction.id} 
            className="p-2 mb-1 border-b"
          >
            <span
              style={{ 
                color: transaction.amount < 0 ? "#FF0000" : "#00C853",
                fontWeight: "bold"
              }}
            >
              {transaction.description}
            </span> - ${Math.abs(transaction.amount).toFixed(2)} ({transaction.category})
          </li>
        ))}
      </ul>
      {sortedTransactions.length === 0 && <p>No transactions found.</p>}

      {/* Chart */}
      {chartData.length > 0 && (
        <div>
          <h3 className="text-lg font-bold mb-2" style={{ color: CHART_COLOR }}>
            {typeFilter === "expense" ? "Expenses" : 
             typeFilter === "income" ? "Income" : 
             "Transactions"} by Category
          </h3>
          
          <PieChart width={400} height={300}>
            <Pie
              data={chartData}
              cx="50%"
              cy="50%"
              outerRadius={80}
              dataKey="value"
              nameKey="name"
              label={({ name, percent }) => `${name} (${(percent * 100).toFixed(0)}%)`}
            >
              {chartData.map((entry, index) => (
                <Cell 
                  key={`cell-${index}`}
                  fill={colorShades[index % colorShades.length]}
                />
              ))}
            </Pie>
            <Tooltip formatter={(value) => [`$${value.toFixed(2)}`, 'Amount']} />
            <Legend />
          </PieChart>
        </div>
      )}
    </div>
  );
};

export default TransactionList;
