import React, { useContext, useState } from 'react';
import { TransactionContext } from '../contexts/TransactionContext';
import './DataManager.css';

const DataManager = () => {
  const { transactions, setTransactions } = useContext(TransactionContext);
  const [statusMessage, setStatusMessage] = useState(null);

  const showStatusMessage = (message, type = 'success') => {
    setStatusMessage({ message, type });
    setTimeout(() => setStatusMessage(null), 3000);
  };

  const exportToJSON = () => {
    const dataStr = JSON.stringify(transactions, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
    downloadFile(dataUri, `transactions_${new Date().toISOString().split('T')[0]}.json`);
    showStatusMessage('Data exported to JSON successfully!');
  };

  const exportToCSV = () => {
    const headers = ['Date', 'Type', 'Category', 'Description', 'Amount'];
    const csvContent = [
      headers.join(','),
      ...transactions.map(transaction => [
        transaction.date,
        transaction.type,
        transaction.category,
        `"${transaction.description}"`,
        transaction.amount
      ].join(','))
    ].join('\n');

    const dataUri = 'data:text/csv;charset=utf-8,'+ encodeURIComponent(csvContent);
    downloadFile(dataUri, `transactions_${new Date().toISOString().split('T')[0]}.csv`);
    showStatusMessage('Data exported to CSV successfully!');
  };

  const exportToPDF = async () => {
    try {
      const { jsPDF } = await import('jspdf');
      const doc = new jsPDF();
      
      // Add title
      doc.setFontSize(16);
      doc.text('Transaction History', 14, 15);
      
      // Add date
      doc.setFontSize(10);
      doc.text(`Generated on: ${new Date().toLocaleDateString()}`, 14, 25);
      
      // Add transactions
      doc.setFontSize(12);
      let y = 40;
      
      transactions.forEach((transaction, index) => {
        if (y > 280) {
          doc.addPage();
          y = 20;
        }
        
        const text = `${transaction.date} - ${transaction.type} - ${transaction.category} - ${transaction.description} - $${transaction.amount}`;
        doc.text(text, 14, y);
        y += 10;
      });
      
      doc.save(`transactions_${new Date().toISOString().split('T')[0]}.pdf`);
      showStatusMessage('Data exported to PDF successfully!');
    } catch (error) {
      showStatusMessage('Error generating PDF: ' + error.message, 'error');
    }
  };

  const downloadFile = (dataUri, fileName) => {
    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', fileName);
    linkElement.click();
  };

  const handleImport = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        let importedData;
        if (file.name.endsWith('.csv')) {
          importedData = parseCSV(e.target.result);
        } else {
          importedData = JSON.parse(e.target.result);
        }

        if (Array.isArray(importedData)) {
          setTransactions(importedData);
          showStatusMessage('Data imported successfully!');
        } else {
          throw new Error('Invalid data format');
        }
      } catch (error) {
        showStatusMessage('Error importing data: ' + error.message, 'error');
      }
    };
    reader.readAsText(file);
  };

  const parseCSV = (csvContent) => {
    const lines = csvContent.split('\n');
    const headers = lines[0].split(',');
    
    return lines.slice(1).map(line => {
      const values = line.split(',');
      return {
        date: values[0],
        type: values[1],
        category: values[2],
        description: values[3].replace(/^"|"$/g, ''), // Remove quotes
        amount: parseFloat(values[4]),
        id: Date.now() + Math.random() // Generate unique ID
      };
    });
  };

  return (
    <div className="data-manager">
      <h2>Data Management</h2>
      
      {statusMessage && (
        <div className={`status-message ${statusMessage.type}`}>
          {statusMessage.message}
        </div>
      )}
      
      <div className="data-actions">
        <button onClick={exportToJSON}>
          <svg viewBox="0 0 24 24" width="24" height="24">
            <path fill="currentColor" d="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z"/>
          </svg>
          Export as JSON
        </button>
        <button onClick={exportToCSV}>
          <svg viewBox="0 0 24 24" width="24" height="24">
            <path fill="currentColor" d="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z"/>
          </svg>
          Export as CSV
        </button>
        <button onClick={exportToPDF}>
          <svg viewBox="0 0 24 24" width="24" height="24">
            <path fill="currentColor" d="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z"/>
          </svg>
          Export as PDF
        </button>
      </div>

      <div className="import-export-section">
        <h3>Import Data</h3>
        <p>Import transaction data from JSON or CSV files</p>
        <input
          type="file"
          accept=".json,.csv"
          onChange={handleImport}
          id="import-input"
          className="file-input"
        />
        <label htmlFor="import-input" className="file-input-label">
          Choose File
        </label>
      </div>

      <div className="import-export-section">
        <h3>Current Data Status</h3>
        <p>Total Transactions: {transactions.length}</p>
        <p>Last Updated: {new Date().toLocaleString()}</p>
      </div>
    </div>
  );
};

export default DataManager;
