import React, { useContext, useState } from "react";
import { TransactionContext } from "../contexts/TransactionContext";

const TransactionHistory = () => {
  const { transactions } = useContext(TransactionContext);
  const [sortType, setSortType] = useState("date"); // Default sorting by date
  const [filterCategory, setFilterCategory] = useState("");

  // Sorting logic
  const sortedTransactions = [...transactions].sort((a, b) => {
    if (sortType === "amount") return b.amount - a.amount;
    if (sortType === "date") return new Date(b.date) - new Date(a.date);
    if (sortType === "category") return a.category.localeCompare(b.category);
    return 0;
  });

  // Filtering logic
  const filteredTransactions = filterCategory
    ? sortedTransactions.filter((transaction) => transaction.category === filterCategory)
    : sortedTransactions;

  return (
    <div style={{ maxHeight: "400px", overflowY: "scroll", border: "1px solid #ccc", padding: "10px" }}>
      <h2>Transaction History</h2>

      {/* Sort and Filter Controls */}
      <div>
        <label>
          Sort by: 
          <select value={sortType} onChange={(e) => setSortType(e.target.value)}>
            <option value="date">Date</option>
            <option value="amount">Amount</option>
            <option value="category">Category</option>
          </select>
        </label>
        <label>
          Filter by Category:
          <input
            type="text"
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
            placeholder="Enter category"
          />
        </label>
      </div>

      {/* Table for Transactions */}
      <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "10px" }}>
        <thead>
          <tr>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Type</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Description</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Amount</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Category</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Date</th>
          </tr>
        </thead>
        <tbody>
          {filteredTransactions.map((transaction, index) => (
            <tr key={index}>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{transaction.type}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{transaction.description}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>${transaction.amount}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{transaction.category}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{transaction.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TransactionHistory;
