import React, { useState, useContext } from "react";
import { TransactionContext } from "../contexts/TransactionContext";
import { PieChart, Pie, Cell, Tooltip } from "recharts";

const TransactionList = () => {
  const { transactions } = useContext(TransactionContext);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("date");

  // Filter transactions based on search input
  const filteredTransactions = transactions.filter((transaction) =>
    transaction.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sort transactions by date or amount
  const sortedTransactions = [...filteredTransactions].sort((a, b) => {
    if (sortBy === "amount") return b.amount - a.amount;
    return new Date(b.date) - new Date(a.date);
  });

  // Prepare data for visualization
  const categories = {};
  transactions.forEach((txn) => {
    categories[txn.category] = (categories[txn.category] || 0) + txn.amount;
  });
  const chartData = Object.keys(categories).map((category) => ({
    name: category,
    value: categories[category],
  }));

  return (
    <div>
      <h2>Transactions</h2>
      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search transactions..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="border p-2 rounded-md w-full mb-4"
      />
      
      {/* Sorting Options */}
      <select
        value={sortBy}
        onChange={(e) => setSortBy(e.target.value)}
        className="border p-2 rounded-md w-full mb-4"
      >
        <option value="date">Sort by Date</option>
        <option value="amount">Sort by Amount</option>
      </select>

      {/* Transactions List */}
      <ul>
        {sortedTransactions.map((transaction) => (
          <li key={transaction.id} className={transaction.type === "expense" ? "expense" : "income"}>
            {transaction.description} - ${Math.abs(transaction.amount).toFixed(2)} ({transaction.category})
          </li>
        ))}
      </ul>
      {sortedTransactions.length === 0 && <p>No transactions found.</p>}

      {/* Expense Category Chart */}
      <h3>Expense Distribution</h3>
      <PieChart width={400} height={400}>
        <Pie
          data={chartData}
          cx="50%"
          cy="50%"
          outerRadius={100}
          fill="#8884d8"
          dataKey="value"
        >
          {chartData.map((entry, index) => (
            <Cell 
              key={`cell-${index}`} 
              fill={entry.value < 0 ? "#FF0000" : "#00C853"} // Red for negative, Green for positive
            />
          ))}
        </Pie>
        <Tooltip />
      </PieChart>
    </div>
  );
};

export default TransactionList;
