import React, { createContext, useState } from "react";

export const TransactionContext = createContext();

export const TransactionProvider = ({ children }) => {
  const [transactions, setTransactions] = useState([]);

  const addTransaction = (transaction) => {
    setTransactions([...transactions, transaction]);
  };

  const calculateBalance = () => {
    const income = transactions
      .filter((t) => t.type === "income")
      .reduce((sum, t) => sum + t.amount, 0);
    const expenses = transactions
      .filter((t) => t.type === "expense")
      .reduce((sum, t) => sum + t.amount, 0);
    return income - expenses;
  };

  const breakdownByCategory = (type) => {
    return transactions
      .filter((t) => t.type === type)
      .reduce((acc, t) => {
        acc[t.category] = (acc[t.category] || 0) + t.amount;
        return acc;
      }, {});
  };

  const breakdownByPeriod = (type, period) => {
    const today = new Date();
    const filteredTransactions = transactions.filter(
      (t) =>
        t.type === type &&
        (period === "daily"
          ? new Date(t.date).toDateString() === today.toDateString()
          : new Date(t.date).getMonth() === today.getMonth() &&
            new Date(t.date).getFullYear() === today.getFullYear())
    );
    return filteredTransactions.reduce((sum, t) => sum + t.amount, 0);
  };

  const searchTransactions = (query) => {
    return transactions.filter((t) =>
      Object.values(t).some((value) =>
        value.toString().toLowerCase().includes(query.toLowerCase())
      )
    );
  };

  return (
    <TransactionContext.Provider
      value={{
        transactions,
        addTransaction,
        calculateBalance,
        breakdownByCategory,
        breakdownByPeriod,
        searchTransactions,
      }}
    >
      {children}
    </TransactionContext.Provider>
  );
};
