import React, { useState, useContext } from "react";
import { TransactionContext } from "../contexts/TransactionContext";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";

const TransactionList = () => {
  const { transactions } = useContext(TransactionContext);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("date");

  // Filter transactions based on search input
  const filteredTransactions = transactions.filter((transaction) =>
    transaction.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sort transactions by date or amount
  const sortedTransactions = [...filteredTransactions].sort((a, b) => {
    if (sortBy === "amount") return b.amount - a.amount;
    return new Date(b.date) - new Date(a.date);
  });

  // Create separate arrays for expenses and income
  const expenses = transactions.filter(txn => txn.amount < 0);
  const income = transactions.filter(txn => txn.amount >= 0);

  // Process expense categories
  const expenseCategories = {};
  expenses.forEach(txn => {
    const category = txn.category;
    if (!expenseCategories[category]) expenseCategories[category] = 0;
    expenseCategories[category] += Math.abs(txn.amount);
  });

  // Process income categories
  const incomeCategories = {};
  income.forEach(txn => {
    const category = txn.category;
    if (!incomeCategories[category]) incomeCategories[category] = 0;
    incomeCategories[category] += txn.amount;
  });

  // Create chart data
  const expenseData = Object.keys(expenseCategories).map(category => ({
    name: category,
    value: expenseCategories[category],
    color: "#FF0000" // Default red for expenses
  }));

  const incomeData = Object.keys(incomeCategories).map(category => ({
    name: category,
    value: incomeCategories[category],
    color: "#00C853" // Default green for income
  }));

  // Custom colors for variety
  const expenseColors = ["#FF0000", "#FF3333", "#FF6666", "#FF9999", "#FFCCCC"];
  const incomeColors = ["#00C853", "#33D975", "#66E398", "#99ECBB", "#CCFFDD"];

  // Custom tooltip
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-white p-2 border rounded shadow">
          <p className="font-bold">{payload[0].name}</p>
          <p style={{ color: payload[0].payload.color }}>
            ${payload[0].value.toFixed(2)}
          </p>
        </div>
      );
    }
    return null;
  };

  return (
    <div>
      <h2>Transactions</h2>
      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search transactions..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="border p-2 rounded-md w-full mb-4"
      />
      
      {/* Sorting Options */}
      <select
        value={sortBy}
        onChange={(e) => setSortBy(e.target.value)}
        className="border p-2 rounded-md w-full mb-4"
      >
        <option value="date">Sort by Date</option>
        <option value="amount">Sort by Amount</option>
      </select>

      {/* Transactions List */}
      <ul>
        {sortedTransactions.map((transaction) => (
          <li 
            key={transaction.id} 
            className="p-2 mb-1 border-b"
          >
            <span
              style={{ 
                color: transaction.amount < 0 ? "#FF0000" : "#00C853",
                fontWeight: "bold"
              }}
            >
              {transaction.description}
            </span> - ${Math.abs(transaction.amount).toFixed(2)} ({transaction.category})
          </li>
        ))}
      </ul>
      {sortedTransactions.length === 0 && <p>No transactions found.</p>}

      {/* Financial Distribution */}
      <div className="mt-8">
        {/* Expense Chart */}
        {expenseData.length > 0 && (
          <div className="mb-8">
            <h3 className="text-lg font-bold text-red-600 mb-2">Expenses by Category</h3>
            <PieChart width={400} height={300}>
              <Pie
                data={expenseData}
                cx="50%"
                cy="50%"
                outerRadius={80}
                dataKey="value"
                nameKey="name"
                label={({ name, percent }) => `${name} (${(percent * 100).toFixed(0)}%)`}
              >
                {expenseData.map((entry, index) => (
                  <Cell 
                    key={`cell-expense-${index}`}
                    fill={expenseColors[index % expenseColors.length]}
                  />
                ))}
              </Pie>
              <Tooltip content={<CustomTooltip />} />
              <Legend />
            </PieChart>
          </div>
        )}
        
        {/* Income Chart */}
        {incomeData.length > 0 && (
          <div>
            <h3 className="text-lg font-bold text-green-600 mb-2">Income by Category</h3>
            <PieChart width={400} height={300}>
              <Pie
                data={incomeData}
                cx="50%"
                cy="50%"
                outerRadius={80}
                dataKey="value"
                nameKey="name"
                label={({ name, percent }) => `${name} (${(percent * 100).toFixed(0)}%)`}
              >
                {incomeData.map((entry, index) => (
                  <Cell 
                    key={`cell-income-${index}`}
                    fill={incomeColors[index % incomeColors.length]}
                  />
                ))}
              </Pie>
              <Tooltip content={<CustomTooltip />} />
              <Legend />
            </PieChart>
          </div>
        )}
      </div>
    </div>
  );
};

export default TransactionList;
