import React, { useContext } from 'react';
import { TransactionContext } from '../contexts/TransactionContext';
import './DataManager.css';

const DataManager = () => {
  const { transactions, setTransactions } = useContext(TransactionContext);

  const handleExport = () => {
    const dataStr = JSON.stringify(transactions, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
    
    const exportFileDefaultName = `transactions_${new Date().toISOString().split('T')[0]}.json`;
    
    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  const handleImport = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const importedData = JSON.parse(e.target.result);
        if (Array.isArray(importedData)) {
          setTransactions(importedData);
          alert('Data imported successfully!');
        } else {
          throw new Error('Invalid data format');
        }
      } catch (error) {
        alert('Error importing data: ' + error.message);
      }
    };
    reader.readAsText(file);
  };

  return (
    <div className="data-manager">
      <h2>Data Management</h2>
      
      <div className="data-actions">
        <div className="action-group">
          <h3>Export Data</h3>
          <p>Download your transaction data as a JSON file</p>
          <button onClick={handleExport} className="export-btn">
            Export Transactions
          </button>
        </div>

        <div className="action-group">
          <h3>Import Data</h3>
          <p>Import transaction data from a JSON file</p>
          <div className="import-container">
            <input
              type="file"
              accept=".json"
              onChange={handleImport}
              id="import-input"
              className="import-input"
            />
            <label htmlFor="import-input" className="import-btn">
              Choose File
            </label>
          </div>
        </div>
      </div>

      <div className="data-info">
        <h3>Current Data Status</h3>
        <p>Total Transactions: {transactions.length}</p>
        <p>Last Updated: {new Date().toLocaleString()}</p>
      </div>
    </div>
  );
};

export default DataManager;
