import React from "react";
import AddTransaction from "./components/AddTransaction";
import TransactionHistory from "./components/TransactionHistory";
import TransactionList from "./components/TransactionList"; // Import the new component
import { TransactionProvider } from "./contexts/TransactionContext";
import Balance from "./components/Balance";
import PieChart from "./components/PieChart"; // Import the PieChart component

const App = () => {
  return (
    <TransactionProvider>
      <div className="app-container">
        <h1>Expense Tracker</h1>
        <Balance />
        <AddTransaction />
        
        {/* Searchable Transaction List */}
        <TransactionList />

        {/* Pie Chart displaying income vs expense */}
        <PieChart />

        {/* Transaction History with Visualization */}
        <TransactionHistory />
      </div>
    </TransactionProvider>
  );
};

export default App;
