import React, { useContext } from "react";
import { Line } from "react-chartjs-2";
import { TransactionContext } from "../contexts/TransactionContext";
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, Title, Tooltip, Legend } from "chart.js";

// Register necessary chart components
ChartJS.register(LineElement, CategoryScale, LinearScale, Title, Tooltip, Legend);

const LineGraph = () => {
  const { transactions } = useContext(TransactionContext);

  // Group transactions by month
  const months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
  const incomeData = new Array(12).fill(0);
  const expenseData = new Array(12).fill(0);

  transactions.forEach((transaction) => {
    const month = new Date(transaction.date).getMonth(); // Get the month index (0 = Jan, 11 = Dec)
    if (transaction.type === "income") {
      incomeData[month] += Math.abs(transaction.amount);
    } else {
      expenseData[month] += Math.abs(transaction.amount);
    }
  });

  // Line chart data
  const data = {
    labels: months,
    datasets: [
      {
        label: "Income",
        data: incomeData,
        borderColor: "#36A2EB",
        backgroundColor: "rgba(54, 162, 235, 0.2)",
        fill: true,
      },
      {
        label: "Expense",
        data: expenseData,
        borderColor: "#FF5733",
        backgroundColor: "rgba(255, 87, 51, 0.2)",
        fill: true,
      },
    ],
  };

  return (
    <div className="line-graph">
      <h3>Income vs Expense Over Time</h3>
      <Line data={data} />
    </div>
  );
};

export default LineGraph;
