import React, { useState, useContext } from "react";
import { TransactionContext } from "../contexts/TransactionContext";
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from "recharts";

const TransactionList = () => {
  const { transactions } = useContext(TransactionContext);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("date");
  const [filterType, setFilterType] = useState("all"); // "all", "expense", or "income"

  // Filter transactions based on search input and type filter
  const filteredTransactions = transactions.filter((transaction) => {
    const matchesSearch = transaction.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = filterType === "all" || transaction.type === filterType;
    return matchesSearch && matchesType;
  });

  // Sort transactions by date or amount
  const sortedTransactions = [...filteredTransactions].sort((a, b) => {
    if (sortBy === "amount") return b.amount - a.amount;
    return new Date(b.date) - new Date(a.date);
  });

  // Prepare data for pie chart based on current filter
  const categoryData = {};
  
  filteredTransactions.forEach((txn) => {
    const category = txn.category || "Uncategorized";
    if (!categoryData[category]) categoryData[category] = 0;
    categoryData[category] += txn.amount;
  });
  
  const chartData = Object.keys(categoryData).map((category) => ({
    name: category,
    value: categoryData[category]
  }));

  // Define colors based on current filter type
  const EXPENSE_COLORS = ['#FF0000', '#FF3333', '#FF6666', '#FF9999', '#FFCCCC'];
  const INCOME_COLORS = ['#00C853', '#33D975', '#66E398', '#99ECBB', '#CCFFDD'];
  
  const COLORS = filterType === "expense" ? EXPENSE_COLORS : INCOME_COLORS;

  return (
    <div>
      <h2>Transactions</h2>
      <div className="filters mb-4 grid grid-cols-1 md:grid-cols-3 gap-4">
        {/* Search Bar */}
        <input
          type="text"
          placeholder="Search transactions..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="border p-2 rounded-md w-full"
        />
        
        {/* Sorting Options */}
        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="border p-2 rounded-md w-full"
        >
          <option value="date">Sort by Date</option>
          <option value="amount">Sort by Amount</option>
        </select>
        
        {/* Type Filter */}
        <select
          value={filterType}
          onChange={(e) => setFilterType(e.target.value)}
          className="border p-2 rounded-md w-full"
        >
          <option value="all">All Transactions</option>
          <option value="expense">Expenses Only</option>
          <option value="income">Income Only</option>
        </select>
      </div>

      {/* Transactions List */}
      <ul className="mb-8">
        {sortedTransactions.map((transaction) => (
          <li 
            key={transaction.id} 
            className="p-2 mb-1 border-b"
          >
            <span
              style={{ 
                color: transaction.type === "expense" ? "#FF0000" : "#00C853",
                fontWeight: "bold"
              }}
            >
              {transaction.description}
            </span> - ${transaction.amount.toFixed(2)} ({transaction.category})
          </li>
        ))}
      </ul>
      {sortedTransactions.length === 0 && <p>No transactions found.</p>}

      {/* Chart */}
      {chartData.length > 0 && (
        <div>
          <h3 
            className="text-lg font-bold mb-2" 
            style={{ color: filterType === "expense" ? "#FF0000" : "#00C853" }}
          >
            {filterType === "expense" ? "Expenses" : 
             filterType === "income" ? "Income" : 
             "Transactions"} by Category
          </h3>
          
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={chartData}
                cx="50%"
                cy="50%"
                outerRadius={80}
                dataKey="value"
                nameKey="name"
                label={({ name, percent }) => `${name} (${(percent * 100).toFixed(0)}%)`}
              >
                {chartData.map((entry, index) => (
                  <Cell 
                    key={`cell-${index}`}
                    fill={COLORS[index % COLORS.length]}
                  />
                ))}
              </Pie>
              <Tooltip formatter={(value) => [`$${value.toFixed(2)}`, 'Amount']} />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
};

export default TransactionList;
