import React, { useContext } from "react";
import { PieChart as RechartsPieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from "recharts";
import { TransactionContext } from "../contexts/TransactionContext";

const PieChart = () => {
  const { transactions } = useContext(TransactionContext);

  // Calculate income and expense totals
  const income = transactions
    .filter((transaction) => transaction.type === "income")
    .reduce((acc, curr) => acc + curr.amount, 0);

  const expense = transactions
    .filter((transaction) => transaction.type === "expense")
    .reduce((acc, curr) => acc + curr.amount, 0);

  // Data for the pie chart
  const data = [
    { name: "Income", value: income },
    { name: "Expense", value: expense },
  ];

  // Define colors for the chart slices
  const COLORS = ["#28a745", "#dc3545"]; // Green for income, Red for expense

  return (
    <ResponsiveContainer width="50%" height={300}> {/* Adjusted width and height */}
      <RechartsPieChart>
        <Pie
          data={data}
          dataKey="value"
          nameKey="name"
          cx="50%" // Centers the pie chart
          cy="50%" // Centers the pie chart
          innerRadius={60}
          outerRadius={100}
          fill="#8884d8"
          label
        >
          {data.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
          ))}
        </Pie>
        <Tooltip />
        <Legend />
      </RechartsPieChart>
    </ResponsiveContainer>
  );
};

export default PieChart;
