import React, { useContext, useState } from "react";
import { TransactionContext } from "../contexts/TransactionContext";

const TransactionHistory = () => {
  const {
    transactions,
    searchTransactions,
    calculateBalance,
    breakdownByCategory,
    breakdownByPeriod,
  } = useContext(TransactionContext);

  const [query, setQuery] = useState("");
  const [filteredTransactions, setFilteredTransactions] = useState(transactions);

  const handleSearch = () => {
    setFilteredTransactions(searchTransactions(query));
  };

  return (
    <div>
      <h2>Transaction History</h2>
      <p>Current Balance: ${calculateBalance()}</p>

      {/* Search Transactions */}
      <input
        type="text"
        placeholder="Search transactions..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      {/* Breakdown */}
      <div>
        <h3>Breakdown</h3>
        <p>Income by Category: {JSON.stringify(breakdownByCategory("income"))}</p>
        <p>Expenses by Category: {JSON.stringify(breakdownByCategory("expense"))}</p>
        <p>Daily Income: ${breakdownByPeriod("income", "daily")}</p>
        <p>Monthly Income: ${breakdownByPeriod("income", "monthly")}</p>
        <p>Daily Expenses: ${breakdownByPeriod("expense", "daily")}</p>
        <p>Monthly Expenses: ${breakdownByPeriod("expense", "monthly")}</p>
      </div>

      {/* Transactions Table */}
      <table>
        <thead>
          <tr>
            <th>Type</th>
            <th>Description</th>
            <th>Amount</th>
            <th>Category</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {filteredTransactions.map((transaction, index) => (
            <tr key={index}>
              <td>{transaction.type}</td>
              <td>{transaction.description}</td>
              <td>${transaction.amount}</td>
              <td>{transaction.category}</td>
              <td>{transaction.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TransactionHistory;
