import React, { useState, useContext } from "react";
import { TransactionContext } from "../contexts/TransactionContext";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";

const TransactionList = () => {
  const { transactions } = useContext(TransactionContext);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("date");

  // Filter transactions based on search input
  const filteredTransactions = transactions.filter((transaction) =>
    transaction.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sort transactions by date or amount
  const sortedTransactions = [...filteredTransactions].sort((a, b) => {
    if (sortBy === "amount") return b.amount - a.amount;
    return new Date(b.date) - new Date(a.date);
  });

  // Prepare data for visualization - fixed version
  const expenseCategories = {};
  const incomeCategories = {};
  
  transactions.forEach((txn) => {
    if (txn.amount < 0) {
      // It's an expense
      const category = txn.category;
      expenseCategories[category] = (expenseCategories[category] || 0) + Math.abs(txn.amount);
    } else {
      // It's income
      const category = txn.category;
      incomeCategories[category] = (incomeCategories[category] || 0) + txn.amount;
    }
  });
  
  const expenseData = Object.keys(expenseCategories).map((category) => ({
    name: category,
    value: expenseCategories[category],
    type: "expense"
  }));
  
  const incomeData = Object.keys(incomeCategories).map((category) => ({
    name: category,
    value: incomeCategories[category],
    type: "income"
  }));

  // Colors
  const EXPENSE_COLOR = "#FF0000";
  const INCOME_COLOR = "#00C853";

  return (
    <div>
      <h2>Transactions</h2>
      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search transactions..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="border p-2 rounded-md w-full mb-4"
      />
      
      {/* Sorting Options */}
      <select
        value={sortBy}
        onChange={(e) => setSortBy(e.target.value)}
        className="border p-2 rounded-md w-full mb-4"
      >
        <option value="date">Sort by Date</option>
        <option value="amount">Sort by Amount</option>
      </select>

      {/* Transactions List */}
      <ul>
        {sortedTransactions.map((transaction) => (
          <li 
            key={transaction.id} 
            style={{ color: transaction.amount < 0 ? EXPENSE_COLOR : INCOME_COLOR }}
          >
            {transaction.description} - ${Math.abs(transaction.amount).toFixed(2)} ({transaction.category})
          </li>
        ))}
      </ul>
      {sortedTransactions.length === 0 && <p>No transactions found.</p>}

      {/* Financial Charts */}
      <div className="flex flex-col md:flex-row justify-between">
        {/* Expense Chart */}
        {expenseData.length > 0 && (
          <div className="w-full md:w-1/2">
            <h3>Expenses by Category</h3>
            <PieChart width={300} height={300}>
              <Pie
                data={expenseData}
                cx="50%"
                cy="50%"
                outerRadius={80}
                fill={EXPENSE_COLOR}
                dataKey="value"
                label={({name, percent}) => `${name} (${(percent * 100).toFixed(0)}%)`}
              >
                {expenseData.map((entry, index) => (
                  <Cell 
                    key={`expense-cell-${index}`} 
                    fill={`rgb(255, ${index * 30}, ${index * 20})`} // Varying shades of red
                  />
                ))}
              </Pie>
              <Tooltip 
                formatter={(value) => [`$${value.toFixed(2)}`, 'Amount']}
              />
            </PieChart>
          </div>
        )}
        
        {/* Income Chart */}
        {incomeData.length > 0 && (
          <div className="w-full md:w-1/2">
            <h3>Income by Category</h3>
            <PieChart width={300} height={300}>
              <Pie
                data={incomeData}
                cx="50%"
                cy="50%"
                outerRadius={80}
                fill={INCOME_COLOR}
                dataKey="value"
                label={({name, percent}) => `${name} (${(percent * 100).toFixed(0)}%)`}
              >
                {incomeData.map((entry, index) => (
                  <Cell 
                    key={`income-cell-${index}`} 
                    fill={`rgb(${index * 20}, ${200 - index * 10}, ${index * 20})`} // Varying shades of green
                  />
                ))}
              </Pie>
              <Tooltip 
                formatter={(value) => [`$${value.toFixed(2)}`, 'Amount']}
              />
            </PieChart>
          </div>
        )}
      </div>
    </div>
  );
};

export default TransactionList;
